import React,{Component} from 'react';
import './App.css';
import Home from './Pages/Home';
import Room from './Pages/Room';
import Host from './Pages/Host';
import Error from './Pages/Error';
import Signin from './Pages/Sinin';
import Signup from './Pages/Signup';
import Roominfo from './Pages/Roominfo';
import Afterloginroom from './Pages/Afterloginroom';
import Profile from './Pages/profile';
import Orders from './Pages/orders';
import HostSignup from './Pages/hostsignup';
import HostSignin from './Pages/hostsignin';
import Property from './Pages/property';
import Hostprofile from './Pages/Hostprofile';
import {Route, Switch} from 'react-router-dom';
import Booking from './Pages/Booking';
import loading from './Coponent/loading';

class App extends Component {
  constructor(){
    super();
    this.state={
      loggedinstatus:"not_logged_in",
      user:{},
      s:[],
      r:[],
      price:0
    }
    this.handlelogstatus=this.handlelogstatus.bind(this);
    this.handlelogstatus1=this.handlelogstatus1.bind(this);
    this.handlehostlogstatus=this.handlehostlogstatus.bind(this);
    this.handlehostlogstatus1=this.handlehostlogstatus1.bind(this);
    this.handles=this.handles.bind(this);
    this.handles_room=this.handles_room.bind(this);
    this.handlelprice=this.handlelprice.bind(this);
    this.handlelogout=this.handlelogout.bind(this);

  }

  handlelogstatus(data){
    this.setState({
      loggedinstatus:"logged_in",
      user:data
    })
    console.log(this.state.user)

  }
  handlelogstatus1(){
    this.setState({
      loggedinstatus:"logged_in"
    })
  }
  handlelprice(data){
    this.setState({
      price:data
    })
  }

  handlehostlogstatus(data){
    this.setState({
      loggedinstatus:"hostlogged_in",
      user:data
    })
    console.log(this.state.user)
  }
  handlehostlogstatus1(){
    this.setState({
      loggedinstatus:"hostlogged_in"
    })
  }
  handles(data){
    this.setState({
      s:data
    });

  }
  handles_room(data){
    console.log(data)
    this.setState({
      r:data
    })
    console.log("room data: ",this.state.r)
  }
  handlelogout(){
    this.setState({
        loggedinstatus:"not_logged_in"
    })
  }

  render() {
    return (<>
      <Switch>
          <Route exact path="/" render={props => (<Home {...props} handles={this.handles} loggedinstatus={this.state.loggedinstatus} handlelogout={this.handlelogout}/> 
          )}/>
          <Route exact path="/room" render={props => (<Room {...props} s={this.state.s} downloadFile={this.downloadFile} loggedinstatus={this.state.loggedinstatus}/>
          )}/>
          <Route exact path="/room/:slug" render={props => (<Roominfo {...props} loggedinstatus={this.state.loggedinstatus} handles_room={this.handles_room}/>
          )}/>
          <Route exact path="/host" render={props => (<Host {...props} loggedinstatus={this.state.loggedinstatus} user={this.state.user}/> )}/>
          <Route exact path="/signin" render={props => (<Signin {...props} user={this.state.user} loggedinstatus={this.state.loggedinstatus}
          handlelogstatus1={this.handlelogstatus1} handlelogout={this.handlelogout}/> 
          )}/>
          <Route exact path="/signup" 
          render={props => (
            <Signup {...props} loggedinstatus={this.state.loggedinstatus} handlelogstatus={this.handlelogstatus} 
            />
          )}/>
          <Route exact path="./myaccount" render={props => (<Afterloginroom {...props} handles={this.handles}/> 
          )}/>
          <Route exact path="/profile" render={props => (<Profile {...props} user={this.state.user} loggedinstatus={this.state.loggedinstatus}/> 
          )}/> 
          <Route exact path="/hostprofile" render={props => (<Hostprofile {...props} user={this.state.user}/> 
          )}/> 
          <Route exact path="/orders" render={props => (<Orders {...props} loggedinstatus={this.state.loggedinstatus} user={this.state.user} r={this.state.r} price={this.state.price} s={this.state.s} />)}/>
          <Route exact path="/hostsignup" render={props => (<HostSignup {...props} loggedinstatus={this.state.loggedinstatus} handlehostlogstatus={this.handlehostlogstatus}/> 
          )}/>
          <Route exact path="/hostsignin" render={props => (<HostSignin {...props} loggedinstatus={this.state.loggedinstatus} handlehostlogstatus1={this.handlehostlogstatus1}/> 
          )}/>
          <Route exact path="/booking" render={props => (<Booking {...props} loggedinstatus={this.state.loggedinstatus} r={this.state.r} s={this.state.s} user={this.state.user} handlelprice={this.handlelprice}/>
          )}/>
          <Route exact path="/property" render={props => (<Property {...props} loggedinstatus={this.state.loggedinstatus} user={this.state.user}/>)}/>
          <Route exact path="/loading" component={loading}/> 

          <Route render={props => (<Error {...props} loggedinstatus={this.state.loggedinstatus}/>)}/>
      </Switch>

      </>
    );
  }
}

export default App;
