import React, { Component } from 'react';
import Navibar from "../Coponent/Navibar";
import Axios from 'axios';
import Postorder from './Postorder';


export default class Orders extends Component {
    constructor(props) {
        super(props)
        this.state = {
            reply:[]  
        }
        this.handle_out=this.handle_out.bind(this);
    }
    
    handle_out(){
        this.props.handlelogout()
    }
    cancelorder(rid){
        let url="http://127.0.0.1:8081/v2/booking/cancelorder";
        const data={"rid":rid,
                    "uid":this.props.uid.email}
        console.log(data)
        
        fetch(url,{
            method: 'post',
            headers:{
                'content-type':'application/json',
                'accept':'application/xml'
            },
            body: JSON.stringify(data)
        }).then((result) => {
            result.json().then((resp)=>{
                console.log(resp)
                if(resp==="Your order has been removed"){
                    alert("Your order has been removed")
                    this.props.history.push("/orders");
                } 
                
            })
        })
    }
    
    
    render() {
        const rep=this.props.order
        return (
            <div className="order">
                <Navibar loggedinstatus={this.props.loggedinstatus} handle_out={this.handle_out}/>
                <div className="order_container">
                    <h2>Your orders</h2>
                    <div >
                        {rep.map((detail,index) => {
                            return <div className="s_div">
                                <div>
                                    <h2>Order: </h2>
                                    <p className="di">{index+1}</p>
                                </div>
                                <br />
                                <div>
                                    <h3>Room id: </h3>
                                    <p className="di">{detail.rid}</p>
                                </div>
                                <div>
                                    <h3>Room name: </h3>
                                    <p className="di">{this.props.r.name}</p>
                                </div>
                                <div>
                                    <h3>Room type: </h3>
                                    <p className="di">{this.props.r.type}</p>
                                </div>
                                
                                <div>
                                    <h3>City: </h3>
                                    <p className="di">{this.props.r.city}</p>
                                </div>
                                <div>
                                    <h3>Suburb: </h3>
                                    <p className="di">{this.props.r.suburb}</p>
                                </div>
                                <div>
                                    <h3>Room address: </h3>
                                    <p className="di">{this.props.r.address}</p>
                                </div>
                                <div>
                                    <h3>Check in date: </h3>
                                    <p className="di">{detail.date_start}</p>
                                </div>
                                <div>
                                    <h3>Check out date: </h3>
                                    <p className="di">{detail.date_end}</p>
                                </div>
                                <div>
                                    <h3>price per night: </h3>
                                    <p className="di">{detail.price}</p>
                                </div>
                                <div>
                                    <h3>Total price: </h3>
                                    <p className="di">{this.props.price}</p>
                                </div>
                                <button className="order_cancel" onClick={()=>this.cancelorder(detail.rid)}>Cancel</button>
                                
                            </div>   
                        })}

                    </div>
                </div>
            </div>
        );
    }
}
