// upload accommodation page


import React, { Component, useState } from 'react'
// import {useContext} from 'react';
// import {RoomContext} from '../Context';
export default class Host extends Component {
    constructor(props) {
        super(props)
        this.state = {
            roomtype: "",
            address: "",
            roomname: "",
            description: "",
            roomprice: "",
            freewifi: false,
            pool: false,
            airconditioner: false,
            kitchen: false,
            gym: false,
            heater: false,
            washingmachine: false,
            iron: false,
            carpark: false,
            image: ""
        }
        this.onCheckChange = this.onCheckChange.bind(this)

    }
    // handle 
    onCheckChange(e) {
        console.log(e.target.checked);
        this.setState({
            [e.target.name]: e.target.checked
        })
    }

    handleInputChange = (event) => {
        event.preventDefault()
        this.setState({
            [event.target.name]: event.target.value
        })
    }


    render() {
        const { roomtype } = this.state
        const { address } = this.state
        const { roomname } = this.state
        const { description } = this.state
        const { roomprice } = this.state

        return (
            <div className="host">
                {/* <div className = "upload"> */}
                <h2>Upload accommodation</h2>
                {/* <form onSubmit={this.handleSubmit1} > */}
                {/* <div className="signup_box"> */}
                <br /> Step1:<br />
                <input type="text" name="roomtype" value={roomtype} placeholder="input room type"
                    onChange={this.handleInputChange} />
                <br /> Step2:<br />
                <input type="text" name="address" value={address} placeholder="input address"
                    onChange={this.handleInputChange} />
                <br />Step3:<br />
                <input type="text" name="roomname" value={roomname} placeholder="input hotel name"
                    onChange={this.handleInputChange} />
                <br />Step4: <br />
                <input type="text" name="description" value={description} placeholder="input descriptions"
                    onChange={this.handleInputChange} />
                <br />Step5: select facilities<br />
                <input type="checkbox" name="freewifi"
                    checked={this.state.freewifi} onChange={this.onCheckChange} /> Free Wifi <br />
                <input type="checkbox" name="pool"
                    checked={this.state.pool} onChange={this.onCheckChange} /> Pool <br />
                <input type="checkbox" name="airconditioner"
                    checked={this.state.airconditioner} onChange={this.onCheckChange} /> Air Conditioner <br />
                <input type="checkbox" name="kitchen"
                    checked={this.state.kitchen} onChange={this.onCheckChange} /> Kitchen <br />
                <input type="checkbox" name="gym"
                    checked={this.state.gym} onChange={this.onCheckChange} /> Gym <br />
                <input type="checkbox" name="heater"
                    checked={this.state.heater} onChange={this.onCheckChange} /> Heater <br />
                <input type="checkbox" name="washingmachine"
                    checked={this.state.washingmachine} onChange={this.onCheckChange} /> Washing Machine <br />
                <input type="checkbox" name="iron"
                    checked={this.state.iron} onChange={this.onCheckChange} /> Iron <br />
                <input type="checkbox" name="carpark"
                    checked={this.state.carpark} onChange={this.onCheckChange} /> Car Park <br />

                <br />Step6: Upload images<br />
                <input type="file" name="file" onChange={(e) => this.onChange(e)} />


                <br />Step7: <br />
                <input type="text" name="roomprice" value={roomprice} placeholder="input price"
                    onChange={this.handleInputChange} />

                <br />
                <button className="submit" onClick={() => { this.handleSubmit1() }}>
                    Submit
                                </button>
                {/* </div>              */}

                {/* </div>         */}
            </div>
        )
    }
}
