import React from 'react';
import {useContext} from 'react';
import {RoomContext} from '../Context';
//get unique values
const getUnique =(items,value)=>{
    return [...new Set(items.map(item => item[value]))];
};

export default function RoomsFilters({rooms}) {
    const context=useContext(RoomContext);
    const {
        handleChange,type,capacity,price,minPrice,maxPrice,
        airconditioner,carpark,wifi,gym,pets,breakfast,kitchen
    } =context;
    //get unique types
    let types=getUnique(rooms,'type');

    //add all
    types=['all',...types];
    //map to jsx
    types=types.map((item,index)=>{
        return <option value={item} key={index}>{item}</option>
    })
    let people =getUnique(rooms,'capacity');
    people= people.map((item,index) =>{
    return <option key={index} value={item}>{item}</option>
    })
        return (
            <section className="filter-container">
                <h2>Search room</h2>
                <form className="filter-form">
                    {/*select city */}
                    <div className="form-group">
                        <label htmlFor="city">city</label>
                        <input type="text" name="city" placeholder="input city name" className="form-control"/>
                    </div>
                    {/*finish select city */}
                    {/*select suburb */}
                    <div className="form-group">
                        <label htmlFor="suburb">suburb</label>
                        <input type="text" name="suburb" placeholder="input suburb name" className="form-control"/>
                    </div>
                    {/*finish select suburb */}
                    {/*select check in */}
                    <div className="form-group">
                        <label htmlFor="checkin">check in date</label>
                        <input type="date" name="checkin" placeholder="input check in date" className="form-control"/>
                    </div>
                    {/*finish select check in */}
                    {/*select check out */}
                    <div className="form-group">
                        <label htmlFor="checkout">check out date</label>
                        <input type="date" name="checkout" placeholder="input check out date" className="form-control"/>
                    </div>
                    <button className="form-control">search</button>
                    {/*finish select check in */}
                </form>
                <h2>Filter by</h2>
                <form className="filter-form">
                    {/*select type*/}
                    <div className="form-group">
                        <label htmlFor="type">room type</label>
                        <select name="type" id="type" value={type}
                        className="form-control" onChange={handleChange}>
                            {types}
                        </select>
                    </div>
                    {/*end of select type*/}
                    {/*select capacity*/}
                    <div className="form-group">
                        <label htmlFor="capacity">Guests</label>
                        <select name="capacity" id="capacity" value={capacity}
                        className="form-control" onChange={handleChange}>
                            {capacity}
                            {people}
                        </select>
                    </div>
                    {/*end of select capacity*/}
                    {/*price */}
                    <div className="form-group">
                        <label htmlFor="price">
                            Room price ${price}
                        </label>
                        <input type="range" name="price" min={minPrice}
                        max={maxPrice} id="price" value={price} onChange={handleChange}
                        className="form-control"/>
                    </div>


                    {/*end of price */}

                </form>
                <form className="filter-form1">
                    {/*extras */}
                    <div className="form-group1">
                        <div className="single-extra">
                            <input type="checkbox" 
                            name="airconditioner" id="airconditioner" 
                            checked={airconditioner} onChange={handleChange}/>
                            <label htmlFor="airconditioner" className="l">
                                airconditioner</label>
                        </div>
                    </div>
                    <div className="form-group1">
                        <div className="single-extra">
                            <input type="checkbox" 
                            name="kitchen" id="kitchen" 
                            checked={kitchen} onChange={handleChange}/>
                            <label htmlFor="kitchen">
                            kitchen</label>
                        </div>
                    </div>
                    <div className="form-group1">
                        <div className="single-extra">
                            <input type="checkbox" 
                            name="carpark" id="carpark" 
                            checked={carpark} onChange={handleChange}/>
                            <label htmlFor="carpark">
                            carpark</label>
                        </div>
                    </div>
                    <div className="form-group1">
                        <div className="single-extra">
                            <input type="checkbox" 
                            name="wifi" id="wifi" 
                            checked={wifi} onChange={handleChange}/>
                            <label htmlFor="wifi">
                            wifi</label>
                        </div>
                    </div>
                    <div className="form-group1">
                        <div className="single-extra">
                            <input type="checkbox" 
                            name="gym" id="gym" 
                            checked={gym} onChange={handleChange}/>
                            <label htmlFor="gym">
                            gym</label>
                        </div>
                    </div>
                    <div className="form-group1">
                        <div className="single-extra">
                            <input type="checkbox" 
                            name="pets" id="pets" 
                            checked={pets} onChange={handleChange}/>
                            <label htmlFor="pets">
                            pets</label>
                        </div>
                    </div>
                    <div className="form-group1">
                        <div className="single-extra">
                            <input type="checkbox" 
                            name="breakfast" id="breakfast" 
                            checked={breakfast} onChange={handleChange}/>
                            <label htmlFor="breakfast">
                            breakfast</label>
                        </div>
                    </div>
                    {/*extras */}
                </form>
            </section>
        );
}
