import React from 'react';
import { Link } from 'react-router-dom';
import defaultImg from '../images/image/room-1.jpeg';
import PropTypes from 'prop-types';

export default function Roomfilter({room}) {

    const {type,price}=room;
    const u1="../images/"+type+"/1.jpg"
    const u2="../images/"+type+"/2.jpg"
    const u3="../images/"+type+"/3.jpg"
    const u4="../images/"+type+"/4.jpg"
    const u5="../images/"+type+"/5.jpg"
    const images=[u1,u2,u3,u4,u5]

    return (
        <article className="rooms">
            <div className="img-container">

                <img src={require("../images/"+type+"/1.jpg")} alt="single room" />
                <div className="price-top">
                    <h5>${price}</h5>
                    <p>per night</p>
                </div>
                <Link to={`/room/${type}`} className="btn-primary room-link">
                    Features
                </Link>
            </div>
            <p className="room-info">{type}</p>
        </article>
    );
}

Roomfilter.propTypes ={
    room:PropTypes.shape({
        name:PropTypes.string.isRequired,
        slug:PropTypes.string.isRequired,
        images:PropTypes.arrayOf(PropTypes.string).isRequired,
        price:PropTypes.number.isRequired,
    })
};
