import React, { Component} from 'react'
import Afternav from "../Coponent/nav_after";

export default class Host extends Component {
    constructor(props) {
        super(props)
        this.state = {
            type: "",
            city: "",
            suburb:"",
            address:"",
            name: "",
            description: "",
            price: "",
            pets: false,
            breakfast: false,
            airconditioner: false,
            carpark: false,
            wifi: false,
            gym: false,
            kitchen: false,
            image: ""
        }
        this.onCheckChange = this.onCheckChange.bind(this)

    }
    // handle 
    onCheckChange(e) {
        console.log(e.target.checked);
        this.setState({
            [e.target.name]: e.target.checked
        })
    }

    handleInputChange = (event) => {
        event.preventDefault()
        this.setState({
            [event.target.name]: event.target.value
        })
    }


    render() {
        const { type } = this.state
        const { address } = this.state
        const { name } = this.state
        const { description } = this.state
        const { roomprice } = this.state

        return (
            <div className="host">
                <Afternav />
                <h2>Upload accommodation</h2>
                <div className="host_border">
                    <h3>Step1:</h3>
                    <input className="inp" type="text" name="type" value={type} placeholder="input room type"
                    onChange={this.handleInputChange} />
                    <h3>Step2:</h3>
                    <input className="inp" type="text" name="address" value={address} placeholder="input address"
                    onChange={this.handleInputChange} />
                    <h3>Step3:</h3>
                    <input className="inp" type="text" name="name" value={name} placeholder="input room name"
                    onChange={this.handleInputChange} />
                    <h3>Step4:</h3>
                    <input className="des" type="text" name="description" value={description} placeholder="input descriptions"
                        onChange={this.handleInputChange} />
                    <h3>Step5: Select Facilities</h3>

                    <div className="single-extra">
                        <input type="checkbox" 
                        name="wifi" id="wifi" 
                        checked={this.state.wifi} onChange={this.onCheckChange}/>
                        <label htmlFor="wifi" className="l">
                            Free wifi</label>
                    </div>
                </div>


                </div>

        );
    }
}
