import React, {Component} from 'react';
import Axios from 'axios';

class Search extends Component{
    constructor(props){
        super(props)
        this.state = {
            city: "",
            suburb: "",
            checkin:"",
            checkout:"",
            querystr:"city=sydney&suburb=maroubra&checkin=2020-04-03&checkout=2020-04-07"
        }
    }


    async getData(){
        const data=this.state
        console.log(data)
        const usp= new URLSearchParams(this.state.querystr)
        for (const [key,value] of usp){
            console.log(`${key} =>${value}`)
        }
        usp.set('city',this.state.city)
        usp.set('suburb',this.state.suburb)
        usp.set('checkin',this.state.checkin)
        usp.set('checkout',this.state.checkout)
        console.log(usp)

        var url="http://127.0.0.1:8081/v2/room/search?"
        url+=usp
        const response=await Axios.get(url,
        {headers: 
            {'content-type':'application/json',
                'accept':'application/xml'
        }
    })
        console.log(response.data)
    }
    handleInputChange = (event) =>{
        event.preventDefault()
        this.setState({
            [event.target.name] : event.target.value
        })
    }
    render(){
        const {city} =this.state
        const {suburb} =this.state
        const {checkin} =this.state
        const {checkout} =this.state

        const data=this.state
        console.log(data)
        const usp= new URLSearchParams(this.state.querystr)
        for (const [key,value] of usp){
            console.log(`${key} =>${value}`)
        }
        usp.set('city',this.state.city)
        usp.set('suburb',this.state.suburb)
        usp.set('checkin',this.state.checkin)
        usp.set('checkout',this.state.checkout)
        console.log(usp)

        var url="http://127.0.0.1:8081/v2/room/search"
        url+=usp
        console.log(url)



    return (
        <div className="back">
            <div className="Search">
                <h2>Find your place</h2>
                <br/>
                <form>
                    <label className="ci"><b>City:</b>
                        <input type="text" name="city" value={city} placeholder="input city name"
                        onChange={this.handleInputChange}/>
                    </label>
                    <br/>
                    <label className="ci"><b>Suburb:</b>
                        <input type="text" name="suburb" value={suburb} placeholder="input suburb name"
                        onChange={this.handleInputChange}/>  
                    </label>
                    <br/>
                    <label className="ci"><b>Check in:</b>
                    <input type="date" name="checkin" value={checkin} placeholder="input check in date"
                    onChange={this.handleInputChange}/>
                    </label>
                    <br/>
                    <label className="ci"><b>Check out:</b>
                        <input type="date" name="checkout" value={checkout} placeholder="input check out date"
                        onChange={this.handleInputChange}/>
                    </label>
                    <br/>
                    <button className="sea_button" >
                        <h3>search</h3>
                    </button>
                </form>        
            </div>
        </div>
    );
    }
}
export default Search;
